INSERT INTO `ticket` (`ID`, `bedrijf`, `persoon`, `telefoon`, `titelticket`, `probleem`, `oplossing`, `status`, `doorgestuurd`) VALUES
(41, 'Hotel Galamadammen', '', '0514521346', 'LCD menu site niet beschrik baar', 'Het LCD menu is niet beschikbaar vanuit de browser', 'Systeem beheerder naar het IP-adres laten zoeken\r\n--------------\r\nSysteem beheerder snapt het niet\r\nVoorstellen voor een pc daar te plaatsen van 150EU zodat we er altijd bij kunnen.\r\n\r\nhelpdesk@arrix.nl\r\n\r\nArix telefoonnummer: 0512582124\r\nterugkoppeling zsm het is druk zei ze\r\n-----------------------------\r\nBarry belt hun omdat ze hun LCD menu player moe zijn. Ze vinden het de investering van Pink-Remote Device niet de beste optie\r\n------------\r\nZe willen niet in een Pink-Remote Device investeren. Ze hebben een order ervan geannuleerd.', 'gesloten', ''),
(42, 'Het Busstation Cafetaria - Eethuis', '', '0622685260', 'snacks  broodjes en vegi scherm', '&lt;p&gt;snack en broodjes scherm opnieuw indelen Ze hebben veel ruimte over op het scherm. Anders indelen. filmpjes plaatsen op de schermen De catagorie vegi moet rechts in de hoek bij snacks prijzen kunnen ook overgenomen worden&lt;/p&gt;', '&lt;p&gt;Lisa gaat ontwerpen - einde van de week af ------ Belt in de middag terug om feedback te geven over de schermen ----- 5 oktober terug bellen om: 13:30 ---- Te vreden alle wijzigingen waren goed.&lt;/p&gt;', 'gesloten', ''),
(43, 'Snackbar Oase', '', '0620725088', 'Kan niet inloggen + players offline', '&lt;p&gt;Kan niet inloggen in het CMS. Players zijn offline + slecht wifi netwerk&lt;/p&gt;', '&lt;p&gt;De klant kon niet inloggen omdat hij zijn wachtwoord vergeten was. Die kon Peter niet resetten i.m.v. hij een wachtwoord vergeten had aangevraagd. De klant heeft een slecht wifi netwerk. Krijgt van KPN Z.M.S te horen wanneer hij een nieuwe modem krijgt. Wij krijgen hierover terugkoppeling Players zijn ook offline. Dit gaan we oppakken zodra de klant een muis bij de hand heeft ----------- Vanmiddag komt KPN langs 5-oktober Klant belt in de 5 oktober in de middag terug Of morgen ochtend Klant kan nu inmiddels wel weer inloggen ------------ Bellen rond 11:00 7-oktober ---- Players zijn weer online&lt;/p&gt;', 'gesloten', 'y'),
(44, 'Suradiya', '', '0655346777', 'Andere kleuren en letter types', '&lt;p&gt;Ander letter kleuren in de themas.&lt;/p&gt;', '&lt;p&gt;Bellen in de ochtend en op het scherm andere letter kleur laten zien van een thema. Half 1 wordt ik terug gebeld. -&lt;/p&gt;\r\n&lt;p&gt;Peter -----&lt;/p&gt;\r\n&lt;p&gt;12:40 gebeld nam niet op&lt;/p&gt;\r\n&lt;p&gt;----------&lt;/p&gt;\r\n&lt;p&gt;Groter lettertype 26px geanimeerde plaatjes Filmpje als achtergrond Foto''s van haar eten moeten transparant Lisa heeft dit vrijdag af -------&lt;/p&gt;\r\n&lt;p&gt;12 oktober Bellen om 12:15&lt;/p&gt;\r\n&lt;p&gt;12 Oktober wordt gebeld om 13:00&lt;/p&gt;\r\n&lt;p&gt;Weten van de nieuwe schermen en zijn te vreden!&lt;/p&gt;', 'gesloten', ''),
(45, 'Het Oude Raedthuys', '', '0622226464', 'Nieuwe player herinstallatie', 'Oude raadhuis bellen\r\n\r\nPlayer opnieuw geflasht\r\nAlle instellingen staan daardoor in het engels ipv in het chinees\r\n\r\nThemas moesten opnieuw erop gezet worden waardoor het langer duurde doordat er wat technische mankementen waren', 'Bellen en kijken hoe het verloopt\r\n----------\r\nBellen als de player is omgedraaid\r\n------\r\n11:54 klant nam niet op - Peter\r\n------\r\n12:55 klant nam niet op\r\n-----\r\n13:00 Klant nam op \r\nUitgepraat over de instellingen dat alles weer in het engels stond dat was mooi vondt de klant.\r\nDat het wat langer duurde vondt hij niet zo erg.\r\n\r\nHij is eventueel geÃÂ¯nteresseerd in nieuwe players voor 150EU per stuk\r\n\r\nPlayer vandaag opsturen 5 oktober 2016', 'gesloten', ''),
(46, 'HK vis specialiteiten', '', '0703543415', 'Oude media in de player zetten', 'Oude media in de player zetten', 'Bellen als het is gedaan.\r\n----\r\n6 oktober is Alex Schot kamp aanwezig\r\n----------\r\nHij weet ervan: 7 oktober', 'gesloten', ''),
(47, 'Elkema - Achter t hoekse', '', '032490191936', 'Niet verbinden met de wifi en heeft geen inlog codes', 'Inlog codes moet u nog ontvangen\r\n\r\nKan niet verbinden met de WiFi', 'Het bedrijf moet nog zijn inlog codes ontvangen van het account: rafaelvierendeels@hotmail.com of veranderen naar een ander account.\r\n\r\nGaat bellen met zijn provider\r\n-----\r\nPlayers zijn online en inlog codes zijn ontvangen\r\n12:30 Peter', 'gesloten', ''),
(48, 'Eetsalon Theo', '', '0626842950', 'Thema', 'Waar is nieuwe thema is gebleven op 1 2. En 3 + 4 zijn offline net zoals de schermen die staan uit.', 'Belt morgen terug. Haar man gaat kijken of hij kan kijken waar het aan ligt. De players en de schermen zijn alle 2 offline.\r\n\r\nContent probleem lag misschien aan de datum. Dat heeft Peter opnieuw ingesteld. De players halen nu de afspeellijst binnen. En misschien dat het probleem nu is opgelost. Als het probleem is opgelost belt ze terug.\r\n---------\r\nBellen rond 11:15', 'gesloten', ''),
(49, 'Truck stop de ketting', 'Henk Sanders', '0599582630', 'Schermen moeten omgewisseld worden door philips', '&amp;lt;p&amp;gt;De schermen zouden omgewisseld moeten worden door Philips. Dit is niet gebeurt door een fout van philips. Die dachten dat wij dat zouden doen. Na een hoop gedoe zou MMD dit alsnog doen. We hebben nu nog steeds geen reactie&amp;amp;nbsp;van hun gehad.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;-------------&amp;lt;br /&amp;gt;Datum: 13-03-2017&amp;lt;br /&amp;gt;Tijd:10:29:50&amp;lt;br /&amp;gt;nossair@idsignage.nl&amp;lt;/p&amp;gt;', '&amp;lt;p&amp;gt;We wachten op reactie van philips voor het omwisselen van de schermen. Als we reactie hebben van Philips moeten we de klant terug bellen.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;12 oktober 13:22&amp;lt;br /&amp;gt;Inmiddels contact gehad met Philips over de schermen. MMD komt met een reactie over 2 dagen en dan hoort de klant van ons. Over omwisselen, want de klanten support kan daar geen toezegging over geven&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;-----------&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;21-10-2016&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Meerdere malen al contact gehad met main tech(MMD). Ze hebben een nieuwe MMD ticket aangemaakt voor de schermen en de foto''s opgestuurd. Wordt doorgestuurd naar het hoofd kantoor en die gaan oordelen. Het eerste scherm wat is geleverd heeft geen vesa. Het 2e scherm wat is geleverd ziet er niet uit en is een oud model. De klant accepteerd dat niet.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Barry die pakt het op door met Philips te belle&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;------&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;24-10-2016&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Peter heeft gebeld met philips. Mevrouw gaat er over praten met haar leiding gevende&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;26-10-16 heb ik reactie&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;-----------&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;26-10-2016&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Het is door gestuurd naar het service centrum. De leidinggevende kon er ook niet veel mee doen. Morgen dus 27-10-2016 bellen met Main tech&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;-----&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;28-10-2016&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Schermen worden omgewisseld. Binnen 2 werkdagen wordt er een afspraak ingepland.&amp;lt;br /&amp;gt;MMD-161028-0027: Is het verkeerde model&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;MMD-161028-0028: Model in slechte staat&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;------&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;31-10-2016&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Schermen worden vervangen. Peter heeft dit gecontroleerd bij Main tech en het gaat om de juiste schermen. Schermen worden binnen 2 werkdagen vervangen.&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;----&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;7-november 2016&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Schermen zijn inmiddels ontvangen&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;-------------&amp;lt;br /&amp;gt;Datum: 13-03-2017&amp;lt;br /&amp;gt;Tijd:10:29:50&amp;lt;br /&amp;gt;nossair@idsignage.nl&amp;lt;/p&amp;gt;\r\n&amp;lt;p&amp;gt;Philips is langsgekomen en heeft het probleem opgelost.&amp;lt;/p&amp;gt;', 'gesloten', 'y'),
(51, 'Emin Oglu', '', '0624671995', 'Nieuwe thema', '&lt;p&gt;De klant wilt graag een nieuw thema. Het oude thema is te oud vindt de klant. De klant wil ook een scherm met filmpjes van aanbiedingen.&lt;/p&gt;\r\n&lt;p&gt;De klant wilt ook voor zijn raam een scherm en een player om klanten naar binnen te halen met week en dag aanbiedingen.&lt;/p&gt;', '&lt;p&gt;Barry neem jij contact op met de klant voor een nieuw scherm met een nieuwe player en een afspraak voor het ontwerpen van zijn nieuwe thema''s&lt;/p&gt;', 'gesloten', ''),
(52, 'Krron vis zandvoort', '', '0631567718', 'Nieuw scherm', '&lt;p&gt;De klant wilt een nieuw scherm omdat de oude kapot is.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Klant wilt het zelfde type tv&amp;nbsp;als de vorige keer.&lt;/p&gt;', '&lt;p&gt;Contact opnemen door barry&lt;/p&gt;', 'gesloten', ''),
(112, 'Hotel galamadame', 'Receptie personeel', '0514521346', 'Schermen werken niet', '&lt;p&gt;De schermen van hotel galamadame werken niet.&lt;/p&gt;\r\n&lt;p&gt;Het personeel zei dat er een update is geweest van het systeem? Daardoor werken de schermen niet meer -- Wij voeren geen updates meer door in het LCD menu.&lt;/p&gt;\r\n&lt;p&gt;Peter -&amp;gt; heeft ook niet kunnen vinden wat er aan de hand was.&lt;/p&gt;', '&lt;p&gt;Peter -&amp;gt; Ik heb het voorstel gedaan dat ze een keer langskomen voor ons nieuwe systeem. De dame van de receptie ging dit ook doorgeven aan haar baas als haar baas tijd heeft.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Barry weet jij nog wat het zou kunnen zijn met de update? Ik kon niks vinden&lt;/p&gt;\r\n&lt;p&gt;test&lt;/p&gt;', 'gesloten', ''),
(113, 'Het friethuys', 'Tom Knoops', '0651241347', 'Player in loop', '&lt;p&gt;Player in startup loop. Word opgestuurd.&lt;/p&gt;', '&lt;p&gt;Wanneer hier; nieuwe flash, testen koppelen, updaten etc.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;------&lt;/p&gt;\r\n&lt;p&gt;21-10-2016 - 15:15&lt;/p&gt;\r\n&lt;p&gt;Player&amp;nbsp;ontvangen&lt;/p&gt;\r\n&lt;p&gt;-----&lt;/p&gt;\r\n&lt;p&gt;24-10-2016&lt;/p&gt;\r\n&lt;p&gt;Klant heeft een vervangende player besteld een minix X5 voor 150EU.&lt;br /&gt;Barry, kan je deze player factureren?&lt;/p&gt;\r\n&lt;p&gt;---------&lt;/p&gt;\r\n&lt;p&gt;26-10-2016&lt;/p&gt;\r\n&lt;p&gt;Player is gefactureerd. Wachten op betaling&lt;/p&gt;\r\n&lt;p&gt;----&lt;/p&gt;\r\n&lt;p&gt;28-10-2016&lt;/p&gt;\r\n&lt;p&gt;Bellen om te vragen hoe het zit met de betaling&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;----&lt;/p&gt;\r\n&lt;p&gt;28-10-2016&lt;/p&gt;\r\n&lt;p&gt;Player wordt betaald op maandag: 31-10-2016&lt;/p&gt;\r\n&lt;p&gt;------------&lt;/p&gt;\r\n&lt;p&gt;2-10-2016&lt;/p&gt;\r\n&lt;p&gt;Player wordt vandaag verzonden, de klant ontvangt de player morgen&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 'gesloten', 'y'),
(114, 'Cafetaria ijsbeer', '', '0642287532', 'Kan niet bij LCD inlog menu', '&lt;p&gt;Kan niet bij LCD inlog menu. Echter was dit eerst wel het geval. ip van kast is 192.168.2.222. Ik heb zelf via teamviewer nog gekeken of er iets aan kon worden gedaan, echter zonder succes. Acces denied. (geen rechten) er is een poort geblokkeerd.&lt;/p&gt;', '&lt;p&gt;Scherm kon niet worden gemaakt&lt;/p&gt;\r\n&lt;p&gt;---- Peter&lt;/p&gt;', 'gesloten', ''),
(115, 'Cafetaria de ijsbeer', 'Adre Feskus', '0642287532', 'Mail sturen met offerte + info', '&lt;p&gt;LCD menu van de klant is kapot. Dit systeem kan niet worden ontgerendeld.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '&lt;p&gt;Klant wilt een mailtje met daarin: offerte en informatie over het nieuwe systeem.&lt;/p&gt;\r\n&lt;p&gt;Email: fesje@msn.com&lt;/p&gt;\r\n&lt;p&gt;Dinsdag bellen&lt;/p&gt;\r\n&lt;p&gt;-------&lt;/p&gt;\r\n&lt;p&gt;26-10-16&lt;/p&gt;\r\n&lt;p&gt;Peter heeft gebeld. De klant neemt nog steeds niet op.&lt;/p&gt;\r\n&lt;p&gt;7-11-2016&lt;/p&gt;\r\n&lt;p&gt;Klant neemt nog steeds niet op&lt;/p&gt;\r\n&lt;p&gt;Peter heeft nog niet kunnen praten met de klant over het nieuwe systeem&lt;/p&gt;\r\n&lt;p&gt;-----------&lt;/p&gt;\r\n&lt;p&gt;22-11-2016&lt;/p&gt;\r\n&lt;p&gt;Klant wilt een prijs opgaven in de mail.&lt;/p&gt;\r\n&lt;p&gt;Voor het all in one pakket voor 2500 euro en voor 350 EURO om zijn player na te kijken&lt;/p&gt;\r\n&lt;p&gt;----------&lt;/p&gt;\r\n&lt;p&gt;6 december 2016&lt;/p&gt;\r\n&lt;p&gt;Klant heeft al iets anders werkend en vond 350Euro te veel geld.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 'gesloten', 'y'),
(118, 'Geytoorn', 'Tim', '0612268321', 'Meneer kan niet inloggen in het LCD menu', '&lt;p&gt;Meneer kan niet meer inloggen in het LCD menu. Dit heeft te maken met dat de acces denied is. Nu is er een oplossing om in de hardeschijf de file permissions te veranderen. Dit heeft Peter doorgestuurd naar Tim&lt;/p&gt;\r\n&lt;p&gt;-----&lt;/p&gt;\r\n&lt;p&gt;1-november 2016&lt;/p&gt;\r\n&lt;p&gt;De HDD kan worden gerepaireerd d.m.v. gparted.&lt;/p&gt;\r\n&lt;p&gt;9 november bellen met de klant, want de klant is op vakantie&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 06-12-2016&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 13-12-2016&lt;br /&gt;Tijd:08:57:54&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 16-12-2016&lt;br /&gt;Tijd:11:48:58&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 20-12-2016&lt;br /&gt;Tijd:13:55:28&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 21-12-2016&lt;br /&gt;Tijd:12:14:12&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 21-12-2016&lt;br /&gt;Tijd:12:15:51&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;', '&lt;p&gt;Nog niet&lt;/p&gt;\r\n&lt;p&gt;--------------&lt;/p&gt;\r\n&lt;p&gt;De klant wilt de player opnieuw laten installeren (LCD menu player).&lt;/p&gt;\r\n&lt;p&gt;Prijs opgaven + waar de klant het de player naar toe kan sturen mailen.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Meneer Smith&lt;/p&gt;\r\n&lt;p&gt;mail:&amp;nbsp;&lt;span style=&quot;color: #545454; font-family: arial, sans-serif; font-size: small;&quot;&gt;info@&lt;/span&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;geythorn&lt;/span&gt;&lt;span style=&quot;color: #545454; font-family: arial, sans-serif; font-size: small;&quot;&gt;.&lt;/span&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;nl&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;------&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;15 november 2016&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;Peter-&amp;gt; prijs opgaven verstuurd&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;----------&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;22 november 2016 15:45&lt;/span&gt;&lt;/p&gt;\r\n&lt;h3&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;Klant neemt niet op.&lt;/span&gt;&lt;/h3&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;-------&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;6 december 2016&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-weight: bold; color: #6a6a6a; font-family: arial, sans-serif;&quot;&gt;Klant zei dat die die player al had laten opsturen door iemand. Hij ging dit vandaag laten controleren.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 06-12-2016&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;Player wordt vandaag opgestuurd we ontvangen hem wss morgen of overmorgen&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 13-12-2016&lt;br /&gt;Tijd:08:57:54&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;De status van de player is alsvolgt:&lt;br /&gt;- Player start niet meer op in het lcdmenu. Dit komt doordat de LCD player een error heeft op de hardeschijf.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 16-12-2016&lt;br /&gt;Tijd:11:48:58&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;Player is kapot en kan niet meer worden gemaakt. Een rapport maken voor de klant zodat we die kunnen leveren als onderbouwing waarom die het niet meer doet.&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 20-12-2016&lt;br /&gt;Tijd:13:55:28&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 21-12-2016&lt;br /&gt;Tijd:12:14:12&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;Bellen met de eigenaar: 0653255081&lt;br /&gt;Hilbert smith&lt;/p&gt;\r\n&lt;p&gt;-------------&lt;br /&gt;Datum: 21-12-2016&lt;br /&gt;Tijd:12:15:51&lt;br /&gt;peter@idsignage.nl&lt;/p&gt;\r\n&lt;p&gt;Eigenaar op de hoogte gesteld dat de player dood is. De klant hoeft de player niet meer&lt;/p&gt;', 'gesloten', 'y'),
(124, 'onbekent (atm)', 'onbekent (atm)', '0624720447', 'Snapt niet hoe men in het systeem komt (LCDMenu)', '&lt;p&gt;Meneer heeft een zaak overgenomen, echter weet hij niet hoe het systeem werkt. inlog codes en etc. weet meneer wel.&lt;/p&gt;', '&lt;p&gt;Hij belt ergens een vrijdag terug. Hij moet even door alle stappen heen geleid worden. Echter weet hij niet welk IP hij nodig heeft (standaard: https://192.168.x.222)&lt;/p&gt;\r\n&lt;p&gt;------&lt;/p&gt;\r\n&lt;p&gt;6-12-2016&lt;/p&gt;\r\n&lt;p&gt;Jeroen heeft gebeld. De klant had nog geen behoefte aan support.&lt;/p&gt;', 'gesloten', ''),
(125, 'Shirleys', 'Shirley', '0630024012', 'IP achterhalen LCDmenu kast', '&lt;p&gt;Shirleys belt op 8 november om half 11 voor hulp bij teamviewer. Dit installeerd zij zelf op haar laptop. Player word gelinkt in het netwerk. Uitlezen van player met standaard methode. Om zo het IP van de kast te achterhalen.&lt;/p&gt;', '&lt;p&gt;uitvoeren, oplossingen via standaard methodes.&lt;/p&gt;\r\n&lt;p&gt;----&lt;/p&gt;\r\n&lt;p&gt;Dit heeft niet gewerkt. Shirley gaat erover nadenken of ze een nieuw systeem wilt aanschaffen&lt;/p&gt;\r\n&lt;p&gt;systeem kan niet worden gevonden&lt;/p&gt;', 'gesloten', 'y'),
(126, 'Hotel Ootmarsum', 'R Bults', '0541291728', 'Nieuwe player opsturen', '&lt;p&gt;De player van&amp;nbsp;Hotel Ootmarsum zit vast op het logo van hello infinity. De klant stuurt maandag zijn player naar ons.&amp;nbsp;Wij sturen daarom maandag een nieuwe minix X5 naar de klant&lt;/p&gt;\r\n&lt;p&gt;-----&lt;/p&gt;\r\n&lt;p&gt;Klant belt 7 november om 11:30 om te vertellen wat hij heeft opgestuurd naar ons.&lt;/p&gt;', '&lt;p&gt;Op het moment dat de binnenkomt flashen&lt;/p&gt;\r\n&lt;p&gt;Player mag verstuurd worden naar:&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;em&gt;&lt;strong&gt;Westwal 1 &lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;em&gt;&lt;strong&gt;7631 BM&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;em&gt;&lt;strong&gt;Ootmarsum&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;em&gt;&lt;strong&gt;---&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;em&gt;&lt;strong&gt;7-november 2016&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;em&gt;Klaar voor verzending&lt;/em&gt;&lt;/p&gt;', 'gesloten', ''),
(127, 'Het pleintje', 'Zoon van Marcel', '0637392592', 'Uitleg CMS', '&lt;p&gt;De zoon van Marcel belt maandag voor uitleg van het systeem&lt;/p&gt;', '&lt;p&gt;CMS uitleggen door Peter of Jeroen&lt;/p&gt;\r\n&lt;p&gt;---------&lt;/p&gt;\r\n&lt;p&gt;8 november 2016&lt;/p&gt;\r\n&lt;p&gt;De zoon van Marcel heeft gisteren niet gebeld&lt;/p&gt;\r\n&lt;p&gt;-----&lt;/p&gt;\r\n&lt;p&gt;Een vriend van Marcel regelt vanaf nu zijn schermen&lt;/p&gt;', 'gesloten', ''),
(128, 'Lunchroom eetcafe', 'Petra Ros-de Deugd', '0637392592', 'LCD menu', '&lt;p&gt;Maandag middag bellen om 13:00.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Ze moeten hun LCD menu updaten, de thema''s. De klant weet zelf niet hoe dit moet.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Ze hebben het volgende mailtje gestuurd:&lt;br /&gt;----------&lt;/p&gt;\r\n&lt;blockquote style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;&lt;strong&gt;Van:&lt;/strong&gt;&amp;nbsp;Petra Ros-de Deugd &amp;lt;&lt;a style=&quot;color: #954f72;&quot; href=&quot;mailto:prosdedeugd@hotmail.com&quot;&gt;prosdedeugd@hotmail.com&lt;/a&gt;&amp;gt;&lt;br /&gt;&lt;strong&gt;Datum:&lt;/strong&gt;&amp;nbsp;4 november 2016 16:02:53 CET&lt;br /&gt;&lt;strong&gt;Aan:&lt;/strong&gt;&amp;nbsp;&quot;&lt;a style=&quot;color: #954f72;&quot; href=&quot;mailto:support@digi-menu.nl&quot;&gt;support@digi-menu.nl&lt;/a&gt;&quot; &amp;lt;&lt;a style=&quot;color: #954f72;&quot; href=&quot;mailto:support@digi-menu.nl&quot;&gt;support@digi-menu.nl&lt;/a&gt;&amp;gt;&lt;br /&gt;&lt;strong&gt;Onderwerp:&lt;/strong&gt;&amp;nbsp;&lt;strong&gt;Help.&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;/blockquote&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;blockquote style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;\r\n&lt;div class=&quot;WordSection1&quot; style=&quot;page: WordSection1;&quot;&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Goede middag,&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Wij hebben twee LCD schermen van LCD MENU. Deze moeten ge&amp;uuml;pdatet worden maar wij krijgen het niet voor elkaar.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Is er een mogelijkheid dat een van jullie medewerkers ons via de telefoon door dit probleem heen helpen.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Graag van tevoren een telefoontje zodat wij aanwezig zijn in het bedrijf.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Alvast bedankt voor de moeite, met vriendelijke groet,&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Petra Ros-de Deugd,&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 0cm 0cm 0.0001pt; font-size: 11pt; font-family: Calibri, sans-serif;&quot;&gt;Lunchroom-Eetcaf&amp;eacute; Plein 10, Nieuw Lekkerland.&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;/blockquote&gt;', '&lt;p&gt;Peter pakt dit op&lt;/p&gt;\r\n&lt;p&gt;11 november bellen om 12:30&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Stappen mailen met daarin hoe ze het LCD menu kunnen updaten&lt;/p&gt;\r\n&lt;p&gt;email:jordy_vdwal@hotmail.com&lt;/p&gt;\r\n&lt;p&gt;------&lt;/p&gt;\r\n&lt;p&gt;15 november 2016&lt;/p&gt;\r\n&lt;p&gt;Peter:&lt;/p&gt;\r\n&lt;p&gt;Mailtje gestuurt met daarin de informatie hoe ze het LCD menu player moeten uodaten&lt;/p&gt;', 'gesloten', 'y'),
(129, 'De fontein', '', '0592315502', 'Nieuw scherm ontwerpen', '&lt;p&gt;Er moeten nieuwe schermen ontworpen worden.&lt;/p&gt;\r\n&lt;p&gt;Moet 7 november 15.00 af.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Is afgemaakt op 10 november.&lt;/p&gt;', '&lt;p&gt;Lisa ontwerpt het.&lt;/p&gt;\r\n&lt;p&gt;----&lt;/p&gt;\r\n&lt;p&gt;Plaatje uitsnijden moet nog gebeuren&lt;/p&gt;', 'gesloten', 'y'),
(130, 'Danny van', 'Yver', '0476554141', 'PostNL pakket niet ontvangen', '&lt;p&gt;Het pakket van POSTNL is volgens de website ontvangen. Echter is deze niet ontvangen door de klant.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Het pakketje is ontvangen door Peter, terwijl de klant Yves heet.&lt;/p&gt;\r\n&lt;p&gt;De klant heeft ook geen &quot;we hebben u gemist kaart gevonden op zijn deurmat&quot;.&lt;/p&gt;\r\n&lt;p&gt;Pakket tracking code: 3SKABA4263555&lt;br /&gt;Postcode: 9630&lt;br /&gt;Land: Belgie&lt;br /&gt;&lt;br /&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Mailtje van de klant:&amp;nbsp;&lt;/p&gt;\r\n&lt;blockquote style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;&lt;strong&gt;Van:&lt;/strong&gt;&amp;nbsp;&quot;Yves&quot; &amp;lt;&lt;a href=&quot;mailto:danyvan@telenet.be&quot;&gt;danyvan@telenet.be&lt;/a&gt;&amp;gt;&lt;br /&gt;&lt;strong&gt;Datum:&lt;/strong&gt;&amp;nbsp;4 november 2016 21:17:07 CET&lt;br /&gt;&lt;strong&gt;Aan:&lt;/strong&gt;&amp;nbsp;&quot;''Barry Contze''&quot; &amp;lt;&lt;a href=&quot;mailto:barry@idsignage.nl&quot;&gt;barry@idsignage.nl&lt;/a&gt;&amp;gt;&lt;br /&gt;&lt;strong&gt;Onderwerp:&lt;/strong&gt;&amp;nbsp;&lt;strong&gt;Antw.:Ã¢ÂÂ¨ Verzend bewijsÃ¢ÂÂ©&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;/blockquote&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;blockquote style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;DAG Barry,&lt;br /&gt;&lt;br /&gt;Ik heb de barcode ingetikt op de website van&amp;nbsp;&lt;a href=&quot;http://internationalparceltracking.com/&quot;&gt;internationalparceltracking.com&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;Volgens PostNl is het pakje op donderdag 3 november afgeleverd en er werd&lt;br /&gt;een handtekening geplaatst en de naam Peter ingevuld&lt;br /&gt;Dit is echter niet in werkelijkheid gebeurt. Ook de buren weten van niets&lt;br /&gt;&lt;br /&gt;Ik heb ook geen afwezigheidskaartje gehad met verdere instructies.&lt;br /&gt;&lt;br /&gt;Ik kan op de website echter niet zien hoe ik een klacht kan indienen of hoe&lt;br /&gt;die chauffeur kan worden benaderd en geconfronteerd met de feiten.&lt;br /&gt;&lt;br /&gt;Het is niet de eerste keer dat chauffeurs zelf &quot;afleveringsbewijzen&quot;&lt;br /&gt;aftekenen en de inhoud voor zichzelf houden, maar wat kan je in hemelsnaam&lt;br /&gt;met een player doen ?&lt;br /&gt;&lt;br /&gt;Wil je zo vriendelijk zijn na het weekend eens te informeren wat me te doen&lt;br /&gt;staat ?&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;Met vriendelijke groeten en prettig weekend gewenst,&lt;br /&gt;&lt;br /&gt;yves&lt;br /&gt;&lt;br /&gt;-----Oorspronkelijk bericht-----&lt;br /&gt;Van: Barry Contze [&lt;a href=&quot;mailto:barry@idsignage.nl&quot;&gt;mailto:barry@idsignage.nl&lt;/a&gt;]&amp;nbsp;&lt;br /&gt;Verzonden: vrijdag 4 november 2016 18:45&lt;br /&gt;Aan:&amp;nbsp;&lt;a href=&quot;mailto:danyvan@telenet.be&quot;&gt;danyvan@telenet.be&lt;/a&gt;&lt;br /&gt;Onderwerp: Verzend bewijs&lt;/blockquote&gt;', '&lt;p&gt;PostNL gaat dit uitzoeken. Over 2 dagen 9-november 2016 krijgen wij hierover terug koppeling&lt;/p&gt;\r\n&lt;p&gt;-----------&lt;/p&gt;\r\n&lt;p&gt;De post bezorger zegt dat hij het in de brievenbus heeft gedaan&lt;/p&gt;\r\n&lt;p&gt;dit is echter niet het geval. PostNL gaat dit verder uitzoeken 9 november&lt;/p&gt;\r\n&lt;p&gt;-----------&lt;/p&gt;\r\n&lt;p&gt;10 november 2016&lt;/p&gt;\r\n&lt;p&gt;Reactie terug van PostNL:&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;Geachte heer Contze,&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;Op uw verzoek heb ik onderzoek gedaan naar uw pakket dat is verzonden met&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;barcode 3SKABA4263555 aan de postcode 9630, te Belgi&amp;euml;.&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;Uit het door mij uitgevoerde onderzoek is gebleken dat het pakket bij de&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;geadresseerde is afgeleverd.&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;De chauffeur geeft aan de zending in de brievenbus te hebben geleverd. De&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;chauffeur is aangesproken op zijn onjuiste werkwijze.&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;Ik hoop u hiermee naar tevredenheid te hebben geholpen en ge&amp;iuml;nformeerd en&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;sluit hierbij het onderzoek.&amp;nbsp;&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;Mocht u nog vragen of opmerkingen hebben dan kunt u ons van maandag tot en&lt;/span&gt;&lt;br style=&quot;font-family: Helvetica; font-size: 12px;&quot; /&gt;&lt;span style=&quot;font-family: Helvetica; font-size: 12px;&quot;&gt;met vrijdag