(131, 'Truckstop de Ketting', 'Henk Sanders', '0599582630', 'Vesa bestelling', '&lt;p&gt;Barry wil jij voor truckstop de ketting de VESA 1115 sturen?&lt;/p&gt;\r\n&lt;p&gt;Deze mag je versturen naar:&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Ter Apel&lt;br /&gt;Rubenbrockerweg 2&lt;br /&gt;9561NT&lt;br /&gt;Groningen&lt;/p&gt;\r\n&lt;p&gt;Gr Peter&lt;/p&gt;', '&lt;p&gt;Bestellen&lt;/p&gt;', 'gesloten', 'y'),
(133, 'EetcafÃÂ© Plein 10', 'Petra Ros-de Deugd', '0184682210', 'Update systeem', '&lt;p class=&quot;MsoNormal&quot;&gt;Goede middag,&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Wij hebben twee LCD schermen van LCD MENU. Deze moeten ge&amp;uuml;pdatet worden maar wij krijgen het niet voor elkaar.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Is er een mogelijkheid dat een van jullie medewerkers ons via de telefoon door dit probleem heen helpen.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Graag van tevoren een telefoontje zodat wij aanwezig zijn in het bedrijf.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Alvast bedankt voor de moeite, met vriendelijke groet,&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Petra Ros-de Deugd,&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Lunchroom-Eetcaf&amp;eacute; Plein 10, Nieuw Lekkerland.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span style=&quot;color: #21201e; font-family: ''Playfair Display'', ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 16px; text-align: center;&quot;&gt;We zijn dagelijks van 10.00 &amp;ndash; 23.00 uur bereikbaar via (0184) 68 22 10.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;8-november 2016&lt;/p&gt;\r\n&lt;p&gt;12:00 bellen&lt;/p&gt;\r\n&lt;p&gt;14 november 2016&lt;/p&gt;\r\n&lt;p&gt;bellen in de middag dan is Petra aanwezig&lt;/p&gt;\r\n&lt;p&gt;----&lt;/p&gt;\r\n&lt;p&gt;dubbel ticket&lt;/p&gt;', 'gesloten', 'y'),
(134, 'Flatmedia', 'Ronald Zuidgeest', '0650290938', 'Cimcool', '&lt;p class=&quot;MsoNormal&quot;&gt;Hoi Barry,&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;De installatie bij Cimcool is niet helemaal vlekkeloos verlopen.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;De player is met een lan kabel aangesloten maar de player gaat regelmatig offline. Na herstart is hij wel weer online.&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Tevens komt de volgende melding steeds kort in beeld:&amp;nbsp;&lt;span style=&quot;font-size: 11.0pt; font-family: ''Calibri'',''sans-serif''; color: #1f497d;&quot;&gt;&amp;ldquo;Superuser granted to Hello&amp;rdquo;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Kan jij mij laten weten hoe we dit kunnen oplossen?&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;Met vriendelijke groet,&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0cm 0cm 12.0pt 0cm;&quot;&gt;&lt;span style=&quot;font-size: 10.0pt;&quot;&gt;Ronald Zuidgeest&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0cm; margin-bottom: .0001pt;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0cm; margin-bottom: .0001pt;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0cm; margin-bottom: .0001pt;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin: 0cm; margin-bottom: .0001pt;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 10.0pt; color: purple;&quot;&gt;Flatmedia&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size: 10.0pt;&quot;&gt;&lt;br